// charts/BasicD3.js
import * as d3 from 'd3';
import React, { Fragment, useEffect, useState } from 'react';
import PropTypes from 'prop-types'

// import './App.css';
// import { drawChart } from './charts/BasicD3';

// export function drawChart(height, width){
