//         .append("text")
//         .attr("fill", "green")
//         .attr("x", 50)
//         .attr("y", 50)
//         .text("Hello D3")
// }

export default function Chart(props) {
    const [data, setData] = useState([]);

    const drawChart = (height, width) => {
        d3.select("#chart")
            .append("svg")
            .attr("width", width)
            .attr("height", height)
            .style("border", "1px solid black")
            .append("text")
            .attr("fill", "green")
            .attr("x", 50)
            .attr("y", 50)
            .text("Hello D3")
    }

    useEffect(() => {
        drawChart()
    }, [props.coordsStr]);


    return (
        <Fragment>
            <h2>Graphs with React</h2>
            <div id="chart">
            </div>
        </Fragment>
    );
}

Chart.propTypes = {
    x: PropTypes.array,
    y: PropTypes.array,
    coordsStr: PropTypes.string
}

Chart.defaultProps = {
    x: [],
    y: [],
    coordsStr: ''
}
