import * as d3 from 'd3';
import React, { Fragment, useEffect, useState, createRef } from 'react';
import PropTypes from 'prop-types'

export default function Chart(props) {
    let chartRef = createRef()

    const [loadedChart, updateLoadStatus] = useState(false),
        [data, setData] = useState([]),
        h = 500,
        w = 400

    const drawChart = () => {
        if(!loadedChart) {
            const accessToRef = d3.select(chartRef.current)
                .append('svg')
                .attr('width', w) // w
                .attr('height', h) // h
                .style('background-color', '#ffffff')
                .style('padding', 10)
                .style('margin-left', 50)

                updateLoadStatus(true)
        }

        if(accessToRef != undefined) {
            accessToRef.selectAll('rect')
                .data([3, 6, 10, 4])
                .enter()
                .append('rect')
                .attr('x', (d, i) => i * 70)
                .attr('y', (d, i) => h - 10 * d)
                .attr('width', 65)
                .attr('height', (d, i) => d * 10)
                .attr('fill', 'tomato')
            }
    }



    // const drawChart = (height, width) => {
    //     d3.select("#chart")
    //         .append("svg")
    //         .attr("width", width)
    //         .attr("height", height)
    //         .style("border", "1px solid black")
    //         .append("text")
    //         .attr("fill", "green")
    //         .attr("x", 50)
    //         .attr("y", 50)
    //         .text("Hello D3")
    // }

    useEffect(() => {
        drawChart()

        console.log('chartRef', chartRef.current)
    }, [props.coordsStr, loadedChart]);


    return (
        <Fragment>
            <h2>Graphs with React</h2>
            <div id="chart" ref={chartRef}></div>
        </Fragment>
    );
}

Chart.propTypes = {
    x: PropTypes.array,
    y: PropTypes.array,
    coordsStr: PropTypes.string
}

Chart.defaultProps = {
    x: [],
    y: [],
    coordsStr: ''
}
