import * as d3 from 'd3';
import React, { Fragment, useEffect, useState, createRef } from 'react';
import PropTypes from 'prop-types'

export default function Chart(props) {
    let chartRef = createRef(),
        accessToRef = null

    const [loadedChart, updateLoadStatus] = useState(false),
        [data, setData] = useState([]),
        h = 500,
        w = 400

    const drawChart = () => {
        if(!loadedChart) {
            accessToRef = d3.select(chartRef.current)
                .append('svg')
                .attr('width', w) // w
                .attr('height', h) // h
                .style('background-color', '#ffffff')
                .style('padding', 10)
                .style('margin-left', 50)

                updateLoadStatus(true)
        }

        if(accessToRef != null) {
            console.log('setting data', props.y)
            accessToRef.selectAll('rect')
                .data(props.y)
                .enter()
                .append('rect')
                .attr('x', (d, i) => i * 70)
                .attr('y', (d, i) => h - 10 * d)
                .attr('width', 65)
                .attr('height', (d, i) => d * 10)
                .attr('fill', 'tomato')
            }
    }

    useEffect(() => {
        drawChart()

        console.log('useEffect chartRef', chartRef.current)
    }, [props.coordsStr, loadedChart]);


    return (
        <Fragment>
            <h2>Graphs with React</h2>
            <div id="chart" ref={chartRef}></div>
        </Fragment>
    );
}

Chart.propTypes = {
    x: PropTypes.array,
    y: PropTypes.array,
    coordsStr: PropTypes.string
}

Chart.defaultProps = {
    x: [],
    y: [],
    coordsStr: ''
}
