import Link from 'next/link'
import { useState, useContext, useEffect, Fragment } from 'react'
import styled from 'styled-components'

const months = ['jan', 'feb', 'mar', 'apr', 'may', 'jun', 'jul', 'aug', 'sep', 'oct', 'nov', 'dec']

export default function Index() {
    const [lat, setLat] = useState(null)
    const [lon, setLon] = useState(null)

    const handleInput = (e) => {
        if(e.target.name === 'lat') {
            setLat(e.target.value)
            console.log(lat)
        } else {
            setLon(e.target.value)
            console.log(lon)
        }
    }

    const handleClick = (e) => {
        e.preventDefault()

        var url = new URL('https://localhost:3000/nrel'),
            params = { lat, lon }

        Object.keys(params).forEach(key => {
            url.searchParams.append(key, params[key])
        })

        fetch(url)
            .then(res => res.json())
            .then(json => {
                console.log(json)
            })
            .catch(err => {
                console.log('error')
                console.error(err)
            })
    }

    useEffect(() => {

    },[])


    return(      
        <Fragment>
            <h3>NREL Data for</h3>
            {/* <p>Latitude {data.inputs.lat} Longitude {data.inputs.lon}</p> */}
            {/* <p>{data.outputs.avg_dni.monthly}</p> */}
            <Form>
                <input type="text" name="lat" placeholder={`Latitude`} onChange={(e)=> { handleInput(e) }} />
                <input type="text" name="lon" placeholder={`Latitude`} onChange={(e)=> { handleInput(e) }} />
                <button type="button" onClick={(e) => { handleClick(e) }}>Search</button>
            </Form>
            <Link href='/about'>
            <a>About</a>
            </Link>
        </Fragment>
    )
}

const Form = styled.form`
    input {
        display: block;
        margin: 0 0 20px;
        border: 0;
        border-bottom: 1px solid rgba(0,0,0,.3);

        &:focus {
            border: 0;
        }
    }
`
